import{_ as a,o as t,c as o,ag as i}from"./chunks/framework.DfsMvb5-.js";const g=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"changelog.md","filePath":"changelog.md"}'),r={name:"changelog.md"};function n(l,e,s,d,c,u){return t(),o("div",null,[...e[0]||(e[0]=[i('<h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to &quot;Changelog&quot;">​</a></h1><p>All notable changes to Movi-Player will be documented in this file.</p><p>The format is based on <a href="https://keepachangelog.com/en/1.0.0/" target="_blank" rel="noreferrer">Keep a Changelog</a>, and this project adheres to <a href="https://semver.org/spec/v2.0.0.html" target="_blank" rel="noreferrer">Semantic Versioning</a>.</p><h2 id="_0-1-4-2026-02-10" tabindex="-1">[0.1.4] - 2026-02-10 <a class="header-anchor" href="#_0-1-4-2026-02-10" aria-label="Permalink to &quot;[0.1.4] - 2026-02-10&quot;">​</a></h2><h3 id="added" tabindex="-1">Added <a class="header-anchor" href="#added" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li><strong>Documentation</strong>: Added comprehensive CORS &amp; Headers section to README and Getting Started guide</li><li><strong>Documentation</strong>: Added Service Worker workaround for COI headers when server modification is not possible</li><li>Service Worker example code for injecting Cross-Origin-Isolation headers client-side</li><li><strong>New Property</strong>: <code>gesturefs</code> attribute to restrict touch gestures (tap/swipe/pinch) to fullscreen mode only</li><li><strong>New Property</strong>: <code>nohotkeys</code> attribute to disable all keyboard shortcuts for playback control</li><li><strong>New Property</strong>: <code>startat</code> attribute to start playback at a specific timestamp (in seconds)</li><li><strong>New Property</strong>: <code>fastseek</code> attribute to enable fast seek controls (±10s skip buttons, keyboard shortcuts, double-tap)</li><li><strong>New Property</strong>: <code>doubletap</code> attribute to enable/disable double-tap to seek gesture</li><li><strong>New Property</strong>: <code>themecolor</code> attribute to customize player UI primary color</li><li><strong>New Property</strong>: <code>buffersize</code> attribute to set custom buffer size in seconds</li><li>Loop functionality with toggle button in control bar and context menu</li></ul><h3 id="changed" tabindex="-1">Changed <a class="header-anchor" href="#changed" aria-label="Permalink to &quot;Changed&quot;">​</a></h3><ul><li>Improved visibility of server requirements for WebAssembly and SharedArrayBuffer usage</li></ul><h3 id="fixed" tabindex="-1">Fixed <a class="header-anchor" href="#fixed" aria-label="Permalink to &quot;Fixed&quot;">​</a></h3><ul><li>WebGL context loss handling on mobile minimize/restore</li><li>Touch control edges secured to prevent conflict with system gestures</li><li>Fast seek counter now correctly stops incrementing at video boundaries (0s and duration)</li><li>Seek OSD icons now consistent between buttons and keyboard shortcuts</li><li>Object fit changes now apply immediately when video is paused</li><li>Buffer visualization no longer shows gaps when seeking in HTTP sources</li><li>Buffer bar now displays continuous range from start instead of sliding window</li><li>Buffer bar never appears behind playback progress indicator</li></ul><h2 id="_0-1-3-2025-01-xx" tabindex="-1">[0.1.3] - 2025-01-XX <a class="header-anchor" href="#_0-1-3-2025-01-xx" aria-label="Permalink to &quot;[0.1.3] - 2025-01-XX&quot;">​</a></h2><h3 id="fixed-1" tabindex="-1">Fixed <a class="header-anchor" href="#fixed-1" aria-label="Permalink to &quot;Fixed&quot;">​</a></h3><ul><li>Improved seek behavior and stabilized buffer visualization</li><li>Fixed hanging in seeking state</li><li>Deferred buffer window creation until network response in source handling</li><li>Updated fast seek icons and poster logic</li></ul><h3 id="added-1" tabindex="-1">Added <a class="header-anchor" href="#added-1" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li>Robust retry logic and buffering state for unstable network connections</li><li>Enhanced mobile experience with new control properties</li></ul><h3 id="changed-1" tabindex="-1">Changed <a class="header-anchor" href="#changed-1" aria-label="Permalink to &quot;Changed&quot;">​</a></h3><ul><li>Updated showcase GIF and documentation</li><li>Documented &#39;auto&#39; decoder mode and seamless fallback UX</li></ul><h2 id="_0-1-2-2024-12-xx" tabindex="-1">[0.1.2] - 2024-12-XX <a class="header-anchor" href="#_0-1-2-2024-12-xx" aria-label="Permalink to &quot;[0.1.2] - 2024-12-XX&quot;">​</a></h2><h3 id="added-2" tabindex="-1">Added <a class="header-anchor" href="#added-2" aria-label="Permalink to &quot;Added&quot;">​</a></h3><ul><li>Initial public release</li><li>WebCodecs + FFmpeg WASM decoding</li><li>HDR detection and rendering support</li><li>Modular design (demuxer, player, element)</li><li>Multi-track audio/subtitle support</li><li>Canvas-based rendering</li><li>Local file playback support</li><li>Professional UI with built-in controls</li></ul><h3 id="supported-formats" tabindex="-1">Supported Formats <a class="header-anchor" href="#supported-formats" aria-label="Permalink to &quot;Supported Formats&quot;">​</a></h3><ul><li><strong>Containers</strong>: MP4, MKV, WebM, MOV, MPEG-TS, AVI, FLV, OGG</li><li><strong>Video Codecs</strong>: H.264, H.265/HEVC, VP8, VP9, AV1</li><li><strong>Audio Codecs</strong>: AAC-LC, MP3, Opus, Vorbis, FLAC, PCM, AC-3, E-AC-3</li><li><strong>Subtitles</strong>: WebVTT, SubRip (SRT), SubStation Alpha (ASS), HDMV PGS, DVD SUB, DVB SUB</li></ul><hr><h2 id="version-history" tabindex="-1">Version History <a class="header-anchor" href="#version-history" aria-label="Permalink to &quot;Version History&quot;">​</a></h2><ul><li><strong>0.1.4</strong> - CORS documentation improvements</li><li><strong>0.1.3</strong> - Seeking fixes and network stability</li><li><strong>0.1.2</strong> - Initial public release</li></ul>',25)])])}const b=a(r,[["render",n]]);export{g as __pageData,b as default};
